import React from "react";
import ValidationError from "./ValidationError";
import config from '../config';

//add onSubmit to form
//add onChange to input field
export default class AddFolder extends React.Component {

  static defaultProps = {
    history: {
      goBack: () => { }
    },
    match: {
      params: {}
    }
  };

  constructor(props) {
    super(props);

    this.state = {

      id: '',
      name: ''
    };
  }

  handleAddFolder(name) {
    const folder = { name };
    const options = {
      method: 'post',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(folder)
    };

    fetch(`${config.API_ENDPOINT}/folders/`, options)
    .then(function (response) {
      return response.json();
    })
      .then(data => {
        this.setState({
          id: " ",
          name: " "
        });
      })
  }




  validateName() {
    const name = this.state.name.trim();
    if (name.length === 0) {
      return "Name is required";
    } else if (name.length < 3) {
      return "Name must be at least 3 characters long";
    }
  }

  updateName(input) {
    this.setState({
      name: input.value,
    });
  }

  handleSubmit(event) {
    event.preventDefault();
    const nameError = this.validateName()
    if (nameError) 
    {this.setState({nameError})
  } else {
    const { name } = this.state;
    document.getElementById("Add").reset();
    this.handleAddFolder(name);
  }
}

  render() {

    return (
      <form
        className="Add-folder"
        id="Add"
        onSubmit={e => this.handleSubmit(e)}
      >
        <label htmlFor="Folder-label">Add Folder Name</label>
        <input
          type="Text"
          className="Folder-input"
          id="Folder-label"
          onChange={e => this.updateName(e.target)}
        ></input>
        {this.state.name && 
          <ValidationError message= {this.state.nameError} />}
        <button
          type="submit"
          className="Add-folder-button"
          disabled= {this.state.nameError}
          onClick={() => this.props.history.goBack()}
        >
          Add Folder
        </button>
      </form>
    );
  }
}
